// On Polygon Amoy (or mainnet later)
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC1820Registry.sol";
import "@openzeppelin/contracts/interfaces/IERC777Recipient.sol";
interface IKlimaRetirementAggregator {
    function retireExactSourceDefault(
        address sourceToken,
        address poolToken,
        uint256 maxAmountIn,
        string memory retiringEntityString,
        address beneficiaryAddress,
        string memory beneficiaryString,
        string memory retirementMessage,
        uint8 fromMode
    ) external returns (uint256);
}
contract AcrossCarbonRetirementReceiver is Ownable, ReentrancyGuard, IERC777Recipient {
    IERC1820Registry private constant ERC1820_REGISTRY =
        IERC1820Registry(0x1820a4B7618BdE71Dce8cdc73aAB6C95905faD24);
    bytes32 private constant TOKENS_RECIPIENT_INTERFACE_HASH =
        keccak256("ERC777TokensRecipient");
    address public immutable KLIMA_AGGREGATOR;
    address public immutable USDC_TOKEN;
    address public immutable DEFAULT_POOL_TOKEN;
    // This will be your custom Polygon “Spoke” / Relayer contract
    address public immutable ACROSS_SPOKE_POOL;
    address public immutable MULTICALL_HANDLER_ADDRESS; // optional, can be address(0)
    event RetirementExecuted(
        address indexed beneficiary,
        string beneficiaryName,
        address poolToken,
        uint256 usdcAmount,
        uint256 carbonAmount
    );
    event RetirementFailed(
        address indexed beneficiary,
        address poolToken,
        uint256 usdcAmount,
        string reason
    );
    constructor(
        address _klimaAggregator,
        address _usdc,
        address _defaultPoolToken,
        address _spokePool,
        address _multicallHandler
    ) Ownable(msg.sender) {
        require(_klimaAggregator != address(0), "invalid klima");
        require(_usdc != address(0), "invalid usdc");
        require(_defaultPoolToken != address(0), "invalid pool");
        require(_spokePool != address(0), "invalid spoke");
        KLIMA_AGGREGATOR = _klimaAggregator;
        USDC_TOKEN = _usdc;
        DEFAULT_POOL_TOKEN = _defaultPoolToken;
        ACROSS_SPOKE_POOL = _spokePool;
        MULTICALL_HANDLER_ADDRESS = _multicallHandler;
        IERC20(USDC_TOKEN).approve(KLIMA_AGGREGATOR, type(uint256).max);
        ERC1820_REGISTRY.setInterfaceImplementer(
            address(this),
            TOKENS_RECIPIENT_INTERFACE_HASH,
            address(this)
        );
    }
    // --- decoding helpers ---
    /**
     * @notice Decode message bytes into beneficiary, beneficiaryName, and poolToken
     * @dev Supports ABI-encoded (address, string, address) format
     * For testing, can also parse simple JSON format: {"beneficiary":"0x...","beneficiaryName":"...","poolToken":"0x..."}
     */
    function decodeMessage(bytes calldata data)
        internal
        view
        returns (
            address beneficiary,
            string memory beneficiaryName,
            address poolToken
        )
    {
        // Try ABI decoding first (most efficient and recommended)
        // Minimum size for ABI encoding: 32 (offset) + 32 (string offset) + 32 (address) + string data
        if (data.length >= 96) {
            try this.tryAbiDecode(data) returns (
                address b,
                string memory bn,
                address pt
            ) {
                return (b, bn, pt);
            } catch {
                // Fall through to JSON parsing if ABI decode fails
            }
        }

        // Fallback: Parse JSON-encoded string
        // JSON format: {"beneficiary":"0x...","beneficiaryName":"...","poolToken":"0x..."}
        string memory jsonStr = string(data);

        beneficiary = extractAddress(jsonStr, "beneficiary");
        beneficiaryName = extractString(jsonStr, "beneficiaryName");
        poolToken = extractAddress(jsonStr, "poolToken");

        // Defaults if extraction fails
        if (beneficiary == address(0)) {
            beneficiary = address(0x000000000000000000000000000000000000dEaD);
        }
        if (bytes(beneficiaryName).length == 0) {
            beneficiaryName = "OffsetZap User";
        }
        if (poolToken == address(0)) {
            poolToken = DEFAULT_POOL_TOKEN;
        }
    }

    /**
     * @notice Try ABI decode helper (external for try-catch)
     */
    function tryAbiDecode(bytes calldata data)
        external
        pure
        returns (
            address beneficiary,
            string memory beneficiaryName,
            address poolToken
        )
    {
        return abi.decode(data, (address, string, address));
    }

    /**
     * @notice Extract address from JSON string by key
     */
    function extractAddress(string memory json, string memory key)
        internal
        pure
        returns (address)
    {
        string memory value = extractString(json, key);
        if (bytes(value).length == 0) {
            return address(0);
        }
        // Remove quotes and 0x prefix handling
        bytes memory valueBytes = bytes(value);
        uint256 start = 0;
        uint256 end = valueBytes.length;

        // Skip leading quotes
        if (valueBytes[0] == '"') start = 1;
        if (valueBytes[end - 1] == '"') end = end - 1;

        // Extract hex address (skip 0x if present)
        if (end - start >= 2 && valueBytes[start] == '0' && valueBytes[start + 1] == 'x') {
            start = start + 2;
        }

        // Parse hex string to address (simplified - assumes valid hex)
        bytes memory addrBytes = new bytes(20);
        uint256 addrIdx = 0;
        for (uint256 i = start; i < end && addrIdx < 20; i += 2) {
            if (i + 1 < end) {
                uint8 high = hexCharToUint(valueBytes[i]);
                uint8 low = hexCharToUint(valueBytes[i + 1]);
                addrBytes[addrIdx++] = bytes1((high << 4) | low);
            }
        }

        // Convert bytes to address
        // Address is rightmost 20 bytes, so we need to pad to 32 bytes first
        bytes memory padded = new bytes(32);
        for (uint256 i = 0; i < 20 && i < addrBytes.length; i++) {
            padded[12 + i] = addrBytes[i]; // Right-align in 32-byte word
        }
        address result;
        assembly {
            result := mload(add(padded, 32))
        }
        return result;
    }

    /**
     * @notice Extract string value from JSON by key
     */
    function extractString(string memory json, string memory key)
        internal
        pure
        returns (string memory)
    {
        bytes memory jsonBytes = bytes(json);
        bytes memory keyBytes = bytes(key);

        // Find key position
        uint256 keyPos = indexOf(jsonBytes, keyBytes);
        if (keyPos == type(uint256).max) {
            return "";
        }

        // Find colon after key
        uint256 colonPos = keyPos + keyBytes.length;
        while (colonPos < jsonBytes.length && jsonBytes[colonPos] != ':') {
            colonPos++;
        }
        if (colonPos >= jsonBytes.length) {
            return "";
        }
        colonPos++; // Skip colon


        // Skip whitespace
        while (colonPos < jsonBytes.length && (jsonBytes[colonPos] == ' ' || jsonBytes[colonPos] == '\t')) {
            colonPos++;
        }

        // Find start of value (quote or start)
        uint256 start = colonPos;
        if (start < jsonBytes.length && jsonBytes[start] == '"') {
            start++; // Skip opening quote
        }

        // Find end of value (quote, comma, or closing brace)
        uint256 end = start;
        while (end < jsonBytes.length) {
            if (jsonBytes[end] == '"' && (end == start || jsonBytes[end - 1] != '\\')) {
                break; // Found closing quote (not escaped)
            }
            if (jsonBytes[end] == ',' || jsonBytes[end] == '}') {
                break;
            }
            end++;
        }

        // Extract substring
        bytes memory result = new bytes(end - start);
        for (uint256 i = 0; i < end - start; i++) {
            result[i] = jsonBytes[start + i];
        }

        return string(result);
    }

    /**
     * @notice Find index of substring in string
     */
    function indexOf(bytes memory haystack, bytes memory needle)
        internal
        pure
        returns (uint256)
    {
        if (needle.length == 0) {
            return 0;
        }
        if (needle.length > haystack.length) {
            return type(uint256).max;
        }

        for (uint256 i = 0; i <= haystack.length - needle.length; i++) {
            bool isMatch = true;
            for (uint256 j = 0; j < needle.length; j++) {
                if (haystack[i + j] != needle[j]) {
                    isMatch = false;
                    break;
                }
            }
            if (isMatch) {
                return i;
            }
        }

        return type(uint256).max;
    }

    /**
     * @notice Convert hex character to uint8
     */
    function hexCharToUint(bytes1 char) internal pure returns (uint8) {
        bytes1 zero = bytes1(uint8(48)); // '0'
        bytes1 nine = bytes1(uint8(57)); // '9'
        bytes1 a = bytes1(uint8(97));    // 'a'
        bytes1 f = bytes1(uint8(102));    // 'f'
        bytes1 A = bytes1(uint8(65));    // 'A'
        bytes1 F = bytes1(uint8(70));    // 'F'

        if (char >= zero && char <= nine) {
            return uint8(char) - uint8(zero);
        }
        if (char >= a && char <= f) {
            return uint8(char) - uint8(a) + 10;
        }
        if (char >= A && char <= F) {
            return uint8(char) - uint8(A) + 10;
        }
        revert("Invalid hex character");
    }

    function executeRetirement(
        uint256 usdcAmount,
        address beneficiary,
        string memory beneficiaryName,
        address poolToken
    ) internal {
        IKlimaRetirementAggregator klimaAggregator =
            IKlimaRetirementAggregator(KLIMA_AGGREGATOR);
        try klimaAggregator.retireExactSourceDefault(
            USDC_TOKEN,
            poolToken,
            usdcAmount,
            "OffsetZap",
            beneficiary,
            beneficiaryName,
            "Carbon offset via OffsetZap",
            0
        ) returns (uint256 carbonAmount) {
            emit RetirementExecuted(
                beneficiary,
                beneficiaryName,
                poolToken,
                usdcAmount,
                carbonAmount
            );
        } catch Error(string memory reason) {
            emit RetirementFailed(
                beneficiary,
                poolToken,
                usdcAmount,
                reason
            );
        } catch {
            emit RetirementFailed(
                beneficiary,
                poolToken,
                usdcAmount,
                "Unknown error"
            );
        }
    }
    function tokensReceived(
        address operator,
        address from,
        address to,
        uint256 amount,
        bytes calldata userData,
        bytes calldata operatorData
    ) external override nonReentrant {
        if (from == ACROSS_SPOKE_POOL && to == address(this)) {
            address beneficiary;
            string memory beneficiaryName;
            address poolToken;
            if (userData.length > 0) {
                (beneficiary, beneficiaryName, poolToken) = decodeMessage(userData);
            } else {
                beneficiary = from;
                beneficiaryName = "OffsetZap User";
                poolToken = DEFAULT_POOL_TOKEN;
            }
            executeRetirement(amount, beneficiary, beneficiaryName, poolToken);
        }
    }
    function handleAcrossTransfer(
        address recipient,
        uint256 amount,
        bytes calldata message
    ) external nonReentrant {
        require(
            msg.sender == ACROSS_SPOKE_POOL ||
                msg.sender == MULTICALL_HANDLER_ADDRESS,
            "Only Spoke or multicall"
        );
        require(recipient == address(this), "Recipient must be this contract");
        uint256 usdcBalance = IERC20(USDC_TOKEN).balanceOf(address(this));
        require(usdcBalance >= amount, "Insufficient USDC balance");
        (
            address beneficiary,
            string memory beneficiaryName,
            address poolToken
        ) = decodeMessage(message);
        executeRetirement(amount, beneficiary, beneficiaryName, poolToken);
    }
    receive() external payable {}
    function manualRetire(
        uint256 usdcAmount,
        address beneficiary,
        string memory beneficiaryName,
        address poolToken
    ) external onlyOwner nonReentrant {
        uint256 usdcBalance = IERC20(USDC_TOKEN).balanceOf(address(this));
        require(usdcBalance >= usdcAmount, "Insufficient USDC balance");
        executeRetirement(usdcAmount, beneficiary, beneficiaryName, poolToken);
    }
    function recoverTokens(address token, uint256 amount)
        external
        onlyOwner
        nonReentrant
    {
        IERC20(token).transfer(owner(), amount);
    }
    function recoverEth() external onlyOwner nonReentrant {
        payable(owner()).transfer(address(this).balance);
    }
}

