contracts/BaseLayerTest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { OApp, Origin, MessagingFee } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OApp.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract BaseLayerTest is OApp {

  string public data;

  // Explicitly pass the owner address to the OApp constructor
  constructor(address _endpoint, address initialOwner) OApp(_endpoint, initialOwner) Ownable(initialOwner) {}

  /// @notice Sends a message from the source chain to the destination chain.
  /// @param _dstEid The endpoint ID of the destination chain.
  /// @param _message The message to be sent.
  /// @param _options The message execution options (e.g. gas to use on destination).
  function sendMessage(uint32 _dstEid, string memory _message, bytes calldata _options) external payable {
     bytes memory _payload = abi.encode(_message); // Encode the message as bytes
     _lzSend(
           _dstEid,
           _payload,
           _options,
           MessagingFee(msg.value, 0), // Fee for the message (nativeFee, lzTokenFee)
           payable(msg.sender) // The refund address in case the send call reverts
     );
  }

  /// @notice Estimates the gas associated with sending a message.
  /// @param _dstEid The endpoint ID of the destination chain.
  /// @param _message The message to be sent.
  /// @param _options The message execution options (e.g. gas to use on destination).
  /// @return nativeFee Estimated gas fee in native gas.
  /// @return lzTokenFee Estimated gas fee in ZRO token.
  function estimateFee(
     uint32 _dstEid,
     string memory _message,
     bytes calldata _options
  ) public view returns (uint256 nativeFee, uint256 lzTokenFee) {
     bytes memory _payload = abi.encode(_message);
     MessagingFee memory fee = _quote(_dstEid, _payload, _options, false);
     return (fee.nativeFee, fee.lzTokenFee);
  }

  /// @notice Entry point for receiving messages.
  /// @param _origin The origin information containing the source endpoint and sender address.
  ///  - srcEid: The source chain endpoint ID.
  ///  - sender: The sender address on the src chain.
  ///  - nonce: The nonce of the message.
  /// @param _guid The unique identifier for the received LayerZero message.
  /// @param payload The payload of the received message.
  /// @param _executor The address of the executor for the received message.
  /// @param _extraData Additional arbitrary data provided by the corresponding executor.
  function _lzReceive(
     Origin calldata _origin,
     bytes32 _guid,
     bytes calldata payload,
     address _executor,
     bytes calldata _extraData
  ) internal override {
     data = abi.decode(payload, (string));
  }
}

contracts/BasePolygonBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title BaseCarbonBridge
 * @dev Upgradeable contract on Base chain for cross-chain carbon credit retirement to Celo & Polygon via LayerZero.
 *      Accepts native ETH and USDC, locks funds, sends retirement requests cross-chain, tracks statuses,
 *      and stores proofs on completion.
 */

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "./interfaces/ILayerZeroReceiver.sol";
import "./interfaces/ILayerZeroEndpoint.sol";

contract BaseCarbonBridge is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    ILayerZeroReceiver
{
    /// -----------------------------------------------------------------------
    /// Events
    /// -----------------------------------------------------------------------
    event RetirementInitiated(
        uint256 indexed requestId,
        address indexed user,
        uint256 amountBase,           // in wei or USDC smallest unit
        uint32 dstChainId,
        string carbonType,
        address beneficiary
    );
    event RetirementCompleted(
        uint256 indexed requestId,
        address indexed user,
        uint256 amountBase,
        uint32 srcChainId,
        bytes proof
    );
    event FeesWithdrawn(address to, uint256 amount);
    event FeePercentUpdated(uint16 newFeeBps);
    event EndpointUpdated(address newEndpoint);
    event EmergencyWithdrawn(address token, address to, uint256 amount);

    /// -----------------------------------------------------------------------
    /// Errors
    /// -----------------------------------------------------------------------
    error InvalidCarbonType();
    error InvalidChain();
    error ZeroAmount();
    error ZeroAddress();
    error NotLZEndpoint();
    error AlreadyCompleted();
    error InsufficientValue();
    error TransferFailed();
    error Unauthorized();

    /// -----------------------------------------------------------------------
    /// Structs & Enums
    /// -----------------------------------------------------------------------
    struct RetirementRequest {
        address user;
        uint256 amount;        // in base chain currency
        uint32 dstChainId;
        string carbonType;
        address beneficiary;
        bool completed;
        bytes proof;
    }

    /// -----------------------------------------------------------------------
    /// State Variables
    /// -----------------------------------------------------------------------

    // LayerZero endpoint on Base
    ILayerZeroEndpoint public lzEndpoint;

    // USDC on Base
    IERC20Upgradeable public usdc;

    // Fee in basis points (default 100 = 1%)
    uint16 public feeBps;

    // auto-incremented request counter
    uint256 public nextRequestId;

    // Mapping requestId => RetirementRequest
    mapping(uint256 => RetirementRequest) public requests;

    // Supported carbon credit types
    mapping(string => bool) public supportedCarbonTypes;

    // Supported destination chain IDs
    mapping(uint32 => bool) public supportedChains;

    /// -----------------------------------------------------------------------
    /// Modifiers
    /// -----------------------------------------------------------------------
    modifier onlyEndpoint() {
        if (msg.sender != address(lzEndpoint)) revert NotLZEndpoint();
        _;
    }

    /// -----------------------------------------------------------------------
    /// Initialization
    /// -----------------------------------------------------------------------
    /**
     * @dev Initialize contract with LayerZero endpoint & USDC address.
     * @param _endpoint LayerZero Endpoint
     * @param _usdc USDC token address on Base
     */
    function initialize(address _endpoint, address _usdc) external initializer {
        if (_endpoint == address(0) || _usdc == address(0)) revert ZeroAddress();
        __Ownable_init();
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        lzEndpoint = ILayerZeroEndpoint(_endpoint);
        usdc = IERC20Upgradeable(_usdc);
        feeBps = 100; //1%
        nextRequestId = 1;

        // Mainnet chains: Celo = 26, Polygon = 109
        supportedChains[26] = true;  // Celo Mainnet
        supportedChains[42220] = true;  // LZ Celo Mainnet
        supportedChains[109] = true; // Polygon Mainnet

        // Testnet chains: Celo Alfajores = 44787, Polygon Amoy = 80002
        supportedChains[44787] = true; // Celo Alfajores
        supportedChains[80002] = true; // Polygon Amoy

        // default supported carbon types
        supportedCarbonTypes["toucan_bct"] = true;
        supportedCarbonTypes["toucan_nct"] = true;
        supportedCarbonTypes["klima_bct"] = true;
    }

    /// -----------------------------------------------------------------------
    /// Public Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Initiate a carbon credit retirement request.
     * @dev User must send native ETH (msg.value) for ETH highways or approve & transfer USDC before calling for USDC.
     * @param useUSDC Set true to use USDC, false for native ETH
     * @param amount Amount of ETH or USDC to use
     * @param dstChainId Destination chain ID (Polygon or Celo)
     * @param carbonType Type of carbon credit to retire
     * @param beneficiary Address on destination chain to retire to
     * @param destinationAddress The address of the contract on the destination chain
     */
    function initiateRetirement(
        bool useUSDC,
        uint256 amount,
        uint32 dstChainId,
        string calldata carbonType,
        address beneficiary,
        bytes calldata destinationAddress
    ) external payable nonReentrant returns (uint256 requestId) {
        if (amount == 0) revert ZeroAmount();
        if (!supportedChains[dstChainId]) revert InvalidChain();
        if (!supportedCarbonTypes[carbonType]) revert InvalidCarbonType();
        if (beneficiary == address(0)) revert ZeroAddress();

        // handle payment
        uint256 paid;
        if (useUSDC) {
            // user must have approved
            usdc.transferFrom(msg.sender, address(this), amount);
            paid = amount;
        } else {
            // native ETH
            if (msg.value != amount) revert InsufficientValue();
            paid = msg.value;
        }

        // compute and deduct fees
        uint256 fee = (paid * feeBps) / 10_000;
        uint256 netAmount = paid - fee;

        // store request
        requestId = nextRequestId++;
        RetirementRequest storage r = requests[requestId];
        r.user = msg.sender;
        r.amount = netAmount;
        r.dstChainId = dstChainId;
        r.carbonType = carbonType;
        r.beneficiary = beneficiary;

        // encode payload: abi.encode(requestId, user, netAmount, carbonType, beneficiary)
        bytes memory payload = abi.encode(
            requestId,
            msg.sender,
            netAmount,
            carbonType,
            beneficiary
        );

        // send cross-chain message
        // Check if the chain ID fits within uint16 range for LayerZero compatibility
        if (dstChainId > type(uint16).max) revert InvalidChain();

        lzEndpoint.send{value: msg.value - (useUSDC ? 0 : fee)}(
            uint16(dstChainId),
            destinationAddress,  // Use the provided destination address
            payload,
            payable(msg.sender),
            address(0),
            bytes("")
        );

        emit RetirementInitiated(requestId, msg.sender, netAmount, dstChainId, carbonType, beneficiary);
    }

    /**
     * @inheritdoc ILayerZeroReceiver
     * @dev LayerZero endpoint calls this when a message arrives from another chain.
     *      Expected payload: abi.encode(requestId, completedProof)
     */
    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64,
        bytes calldata payload
    ) external override onlyEndpoint nonReentrant {
        (uint256 requestId, bytes memory proof) = abi.decode(payload, (uint256, bytes));
        RetirementRequest storage r = requests[requestId];

        if (r.completed) revert AlreadyCompleted();
        r.completed = true;
        r.proof = proof;

        // Convert uint16 to uint32 for our internal representation
        uint32 srcChainId = uint32(_srcChainId);
        emit RetirementCompleted(requestId, r.user, r.amount, srcChainId, proof);
    }

    /// -----------------------------------------------------------------------
    /// View Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Returns status & proof of a given request
     * @param requestId The retirement request ID
     */
    function getRequest(uint256 requestId)
        external
        view
        returns (
            address user,
            uint256 amount,
            uint32 dstChainId,
            string memory carbonType,
            address beneficiary,
            bool completed,
            bytes memory proof
        )
    {
        RetirementRequest storage r = requests[requestId];
        return (r.user, r.amount, r.dstChainId, r.carbonType, r.beneficiary, r.completed, r.proof);
    }

    /// -----------------------------------------------------------------------
    /// Admin Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Withdraw accumulated fees (ETH & USDC) to owner
     * @param to Recipient address
     */
    function withdrawFees(address payable to) external onlyOwner nonReentrant {
        if (to == address(0)) revert ZeroAddress();

        // withdraw native ETH
        uint256 balEth = address(this).balance;
        if (balEth > 0) {
            (bool sent,) = to.call{value: balEth}("");
            if (!sent) revert TransferFailed();
        }

        // withdraw USDC
        uint256 balUsdc = usdc.balanceOf(address(this));
        if (balUsdc > 0) {
            usdc.transfer(to, balUsdc);
        }

        emit FeesWithdrawn(to, balEth + balUsdc);
    }

    /**
     * @notice Owner can add/remove supported carbon types
     */
    function setCarbonType(string calldata carbonType, bool enabled) external onlyOwner {
        supportedCarbonTypes[carbonType] = enabled;
    }

    /**
     * @notice Owner can add/remove supported destination chains
     */
    function setSupportedChain(uint32 chainId, bool enabled) external onlyOwner {
        supportedChains[chainId] = enabled;
    }

    /**
     * @notice Update fee BPS (max 1000 = 10%)
     */
    function setFeeBps(uint16 newFeeBps) external onlyOwner {
        require(newFeeBps <= 1000, "Fee too high");
        feeBps = newFeeBps;
        emit FeePercentUpdated(newFeeBps);
    }

    /**
     * @notice Update LayerZero endpoint
     */
    function setEndpoint(address newEndpoint) external onlyOwner {
        if (newEndpoint == address(0)) revert ZeroAddress();
        lzEndpoint = ILayerZeroEndpoint(newEndpoint);
        emit EndpointUpdated(newEndpoint);
    }

    /// -----------------------------------------------------------------------
    /// Emergency Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Emergency withdraw stuck tokens or ETH
     */
    function emergencyWithdraw(address token, address to, uint256 amount) external onlyOwner nonReentrant {
        if (to == address(0)) revert ZeroAddress();

        if (token == address(0)) {
            // withdraw ETH
            (bool sent,) = to.call{value: amount}("");
            if (!sent) revert TransferFailed();
        } else {
            IERC20Upgradeable(token).transfer(to, amount);
        }

        emit EmergencyWithdrawn(token, to, amount);
    }

    /// -----------------------------------------------------------------------
    /// Upgradeability
    /// -----------------------------------------------------------------------

    function _authorizeUpgrade(address) internal override onlyOwner {}

    /// -----------------------------------------------------------------------
    /// Fallback to accept native ETH payments
    /// -----------------------------------------------------------------------
    receive() external payable {}
}

contracts/CarbonRetirementFacilitatorCel.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/*
   CarbonRetirementFacilitatorCeloUpgradeable.sol

   An upgradeable facilitator for Toucan protocol retirements on Celo,
   extended with LayerZero cross-chain message reception from a Base bridge
   and confirmation messages back to Base.

   Key features:
   - Direct retirements via purchaseAndRetire()
   - Fee-on-top mechanism
   - Admin controls (owner-only)
   - LayerZero integration (ILayerZeroReceiver) for cross-chain retirements
   - Validation of incoming messages from the authorized Base bridge
   - Emission of CrossChainRetirementReceived and CrossChainRetirementCompleted
   - Confirmation messages sent back to Base
   - ReentrancyGuard and Checks-Effects-Interactions pattern
*/

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/ILayerZeroReceiver.sol";
import "./interfaces/ILayerZeroEndpoint.sol";
import "./interfaces/IToucanContractRegistry.sol";
import "./interfaces/ICarbonProjectVintages.sol";
import "./interfaces/IRetirementCertificate.sol";

/**
 * @title CarbonRetirementFacilitatorCeloUpgradeable
 * @notice Processes Toucan retirements on Celo, supports direct and cross-chain
 * @dev Upgradeable. Integrates LayerZero for cross-chain messages.
 */
contract CarbonRetirementFacilitatorCeloUpgradeable is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    ILayerZeroReceiver
{
    /// -----------------------------------------------------------------------
    /// Errors
    /// -----------------------------------------------------------------------
    error ZeroAddressNotAllowed();
    error InvalidFee();
    error UnauthorizedBridge();
    error InvalidPayload();
    error RetirementFailed();
    error ConfirmationFailed();
    error SendingMessageFailed();

    /// -----------------------------------------------------------------------
    /// Events
    /// -----------------------------------------------------------------------
    event FeeUpdated(uint256 newFeeBasisPoints);
    event DirectRetirement(
        address indexed beneficiary,
        address indexed projectVintage,
        uint256 amount,
        string retireMessage
    );
    event CrossChainRetirementReceived(
        uint16 srcChainId,
        bytes srcAddress,
        address beneficiary,
        address projectVintage,
        uint256 amount,
        string retireMessage
    );
    event CrossChainRetirementCompleted(
        uint16 dstChainId,
        bytes dstAddress,
        address beneficiary,
        address projectVintage,
        uint256 amount,
        string retireMessage
    );

    /// -----------------------------------------------------------------------
    /// Storage
    /// -----------------------------------------------------------------------
    // Toucan registry to look up contract addresses
    IToucanContractRegistry public registry;
    // Fee in basis points (e.g., 50 = 0.5%)
    uint256 public feeBasisPoints;
    // LayerZero endpoint for Celo
    ILayerZeroEndpoint public lzEndpoint;
    // Authorized bridge address on Base (as bytes)
    bytes public authorizedBridgeAddress;
    // Destination Chain ID for confirmations (Base chain ID)
    uint16 public baseChainId;

    /// -----------------------------------------------------------------------
    /// Modifiers
    /// -----------------------------------------------------------------------
    modifier nonZeroAddress(address _addr) {
        if (_addr == address(0)) revert ZeroAddressNotAllowed();
        _;
    }

    modifier onlyBridge(uint16 _srcChainId, bytes calldata _srcAddress) {
        // Validate chain ID
        if (_srcChainId != baseChainId || keccak256(_srcAddress) != keccak256(authorizedBridgeAddress))
            revert UnauthorizedBridge();
        _;
    }

    /// -----------------------------------------------------------------------
    /// Initialization
    /// -----------------------------------------------------------------------
    /**
     * @notice Initialize the facilitator
     * @param _registry Toucan contract registry
     * @param _feeBasisPoints fee on top of retirement amount
     * @param _lzEndpoint LayerZero endpoint on Celo
     * @param _baseChainId LayerZero chain ID for Base
     * @param _authorizedBridgeAddress the Base bridge address (bytes)
     */
    function initialize(
        address _registry,
        uint256 _feeBasisPoints,
        address _lzEndpoint,
        uint16 _baseChainId,
        bytes calldata _authorizedBridgeAddress
    ) external initializer {
        __Ownable_init();
        __ReentrancyGuard_init();

        registry = IToucanContractRegistry(_registry);
        _setFee(_feeBasisPoints);
        lzEndpoint = ILayerZeroEndpoint(_lzEndpoint);
        baseChainId = _baseChainId;
        authorizedBridgeAddress = _authorizedBridgeAddress;
    }

    /// -----------------------------------------------------------------------
    /// Admin functions
    /// -----------------------------------------------------------------------
    /**
     * @notice Update fee in basis points
     * @param _newFeeBasisPoints new fee (bps)
     */
    function setFee(uint256 _newFeeBasisPoints) external onlyOwner {
        _setFee(_newFeeBasisPoints);
    }

    function _setFee(uint256 _bps) internal {
        if (_bps > 10_000) revert InvalidFee();
        feeBasisPoints = _bps;
        emit FeeUpdated(_bps);
    }

    /**
     * @notice Update authorized bridge parameters
     * @param _chainId LayerZero chain ID for Base
     * @param _bridgeAddress Base bridge address in bytes
     */
    function setBridgeConfig(uint16 _chainId, bytes calldata _bridgeAddress) external onlyOwner {
        baseChainId = _chainId;
        authorizedBridgeAddress = _bridgeAddress;
    }

    /// -----------------------------------------------------------------------
    /// Direct retirement
    /// -----------------------------------------------------------------------
    /**
     * @notice Purchase and retire a Toucan carbon token
     * @param projectVintage address of project vintage
     * @param amount amount of TCO2 tokens
     * @param retireMessage optional retirement message
     */
    function purchaseAndRetire(
        address projectVintage,
        uint256 amount,
        string calldata retireMessage
    ) external nonReentrant {
        if (projectVintage == address(0)) revert ZeroAddressNotAllowed();
        if (amount == 0) revert InvalidPayload();

        // Transfer tokens from user
        IERC20(projectVintage).transferFrom(msg.sender, address(this), amount);

        // Calculate fee and net
        uint256 fee = (amount * feeBasisPoints) / 10_000;
        uint256 netAmount = amount - fee;

        // Approve and retire via Toucan registry
        ICarbonProjectVintages(projectVintage).approve(address(registry), netAmount);
        registry.retire(projectVintage, netAmount, msg.sender, retireMessage);

        // Send fee to owner
        if (fee > 0) {
            IERC20(projectVintage).transfer(owner(), fee);
        }

        emit DirectRetirement(msg.sender, projectVintage, netAmount, retireMessage);
    }

    /// -----------------------------------------------------------------------
    /// Cross-chain reception (ILayerZeroReceiver)
    /// -----------------------------------------------------------------------
    /**
     * @notice LayerZero receiver entry point
     * @dev Validates and dispatches to _handleCrossChainRetirement
     * @param _srcChainId chain ID of sender
     * @param _srcAddress sender address in bytes
     * @param _payload encoded retirement request
     */
    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64,
        bytes calldata _payload
    ) external override {
        // only endpoint can call
        if (msg.sender != address(lzEndpoint)) revert UnauthorizedBridge();
        // only authorized bridge
        if (_srcChainId != baseChainId || keccak256(_srcAddress) != keccak256(authorizedBridgeAddress))
            revert UnauthorizedBridge();

        // decode payload: (address beneficiary, address projectVintage, uint256 amount, string retireMessage)
        (address beneficiary, address projectVintage, uint256 amount, string memory retireMessage) =
            abi.decode(_payload, (address, address, uint256, string));

        // payload validation
        if (beneficiary == address(0) || projectVintage == address(0) || amount == 0) revert InvalidPayload();

        emit CrossChainRetirementReceived(_srcChainId, _srcAddress, beneficiary, projectVintage, amount, retireMessage);

        // process retirement
        _processCrossChainRetirement(beneficiary, projectVintage, amount, retireMessage, _srcAddress);
    }

    /// -----------------------------------------------------------------------
    /// Internal cross-chain processing
    /// -----------------------------------------------------------------------
    function _processCrossChainRetirement(
        address beneficiary,
        address projectVintage,
        uint256 amount,
        string memory retireMessage,
        bytes calldata srcAddress
    ) internal nonReentrant {
        // Transfer tokens that the bridge pre-funded to facilitator
        IERC20(projectVintage).transferFrom(toAddress(authorizedBridgeAddress), address(this), amount);

        // Calculate fee and net
        uint256 fee = (amount * feeBasisPoints) / 10_000;
        uint256 netAmount = amount - fee;

        // Approve & retire
        ICarbonProjectVintages(projectVintage).approve(address(registry), netAmount);
        try registry.retire(projectVintage, netAmount, beneficiary, retireMessage) {
            // send fee to owner
            if (fee > 0) IERC20(projectVintage).transfer(owner(), fee);
        } catch {
            revert RetirementFailed();
        }

        // send confirmation back to Base
        _sendConfirmation(srcAddress, beneficiary, projectVintage, netAmount, retireMessage);

        emit CrossChainRetirementCompleted(baseChainId, srcAddress, beneficiary, projectVintage, netAmount, retireMessage);
    }

    /// -----------------------------------------------------------------------
    /// Send confirmation
    /// -----------------------------------------------------------------------
    function _sendConfirmation(
        bytes calldata dstAddress,
        address beneficiary,
        address projectVintage,
        uint256 amount,
        string memory retireMessage
    ) internal {
        bytes memory payload = abi.encode(beneficiary, projectVintage, amount, retireMessage);

        // zero adaptorParams = default
        uint16 version = 1;
        bytes memory adapterParams = abi.encodePacked(version, uint256(200_000)); // gas limit for remote

        try
            lzEndpoint.send{value: msg.value}(
                baseChainId,           // destination chain
                dstAddress,            // destination contract
                payload,               // message payload
                payable(msg.sender),   // refund address
                address(0),            // zroPaymentAddress
                adapterParams          // adapterParams
            )
        {
            // success
        } catch {
            revert SendingMessageFailed();
        }
    }

    /// -----------------------------------------------------------------------
    /// Utilities
    /// -----------------------------------------------------------------------
    /**
     * @dev Convert bytes to address (expects right padded 20 bytes)
     */
    function toAddress(bytes memory bys) internal pure returns (address addr) {
        require(bys.length == 20, "Invalid address length");
        assembly {
            addr := mload(add(bys, 20))
        }
    }

    // Allow contract to receive native tokens for LayerZero gas
    receive() external payable {}
    fallback() external payable {}
}

contracts/interfaces/ICarbonPool.sol.content:
// SPDX-License-Identifier: MIT


pragma solidity ^0.8.0;


import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


/**
 * @title ICarbonPool
 * @dev Interface for the Carbon Pool contract
 */
interface ICarbonPool {
    /**
     * @notice Deposits tokens into the carbon pool
     * @param _amount Amount of tokens to deposit
     * @return poolTokens The amount of pool tokens minted
     */
    function deposit(uint256 _amount) external returns (uint256 poolTokens);
   
    /**
     * @notice Withdraws tokens from the carbon pool
     * @param _amount Amount of pool tokens to burn
     * @return underlyingTokens The amount of underlying tokens returned
     */
    function withdraw(uint256 _amount) external returns (uint256 underlyingTokens);
   
    /**
     * @notice Gets the underlying token of the pool
     * @return tokenAddress The address of the underlying token
     */
    function getUnderlyingToken() external view returns (address tokenAddress);
   
    /**
     * @notice Gets the current exchange rate between pool tokens and underlying tokens
     * @return rate The exchange rate (in basis points)
     */
    function getExchangeRate() external view returns (uint256 rate);
   
    /**
     * @notice Converts an amount of pool tokens to the equivalent amount of underlying tokens
     * @param _poolTokenAmount Amount of pool tokens
     * @return underlyingAmount The equivalent amount of underlying tokens
     */
    function convertToUnderlying(uint256 _poolTokenAmount) external view returns (uint256 underlyingAmount);
   
    /**
     * @notice Converts an amount of underlying tokens to the equivalent amount of pool tokens
     * @param _underlyingAmount Amount of underlying tokens
     * @return poolTokenAmount The equivalent amount of pool tokens
     */
    function convertToPoolToken(uint256 _underlyingAmount) external view returns (uint256 poolTokenAmount);
   
    /**
     * @notice Gets the amount of carbon tonnes retired by a specific address
     * @param _beneficiary The address that received the retirement credit
     * @return tonnes The amount of carbon tonnes retired
     */
    function tonnesRetired(address _beneficiary) external view returns (uint256 tonnes);
}

