        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/BasePolygonBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title BaseCarbonBridge
 * @dev Upgradeable contract on Base chain for cross-chain carbon credit retirement to Celo & Polygon via LayerZero.
 *      Accepts native ETH and USDC, locks funds, sends retirement requests cross-chain, tracks statuses,
 *      and stores proofs on completion.
 */

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "./interfaces/ILayerZeroReceiver.sol";
import "./interfaces/ILayerZeroEndpoint.sol";

contract BaseCarbonBridge is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    ILayerZeroReceiver
{
    /// -----------------------------------------------------------------------
    /// Events
    /// -----------------------------------------------------------------------
    event RetirementInitiated(
        uint256 indexed requestId,
        address indexed user,
        uint256 amountBase,           // in wei or USDC smallest unit
        uint32 dstChainId,
        string carbonType,
        address beneficiary
    );
    event RetirementCompleted(
        uint256 indexed requestId,
        address indexed user,
        uint256 amountBase,
        uint32 srcChainId,
        bytes proof
    );
    event FeesWithdrawn(address to, uint256 amount);
    event FeePercentUpdated(uint16 newFeeBps);
    event EndpointUpdated(address newEndpoint);
    event EmergencyWithdrawn(address token, address to, uint256 amount);

    /// -----------------------------------------------------------------------
    /// Errors
    /// -----------------------------------------------------------------------
    error InvalidCarbonType();
    error InvalidChain();
    error ZeroAmount();
    error ZeroAddress();
    error NotLZEndpoint();
    error AlreadyCompleted();
    error InsufficientValue();
    error TransferFailed();
    error Unauthorized();

    /// -----------------------------------------------------------------------
    /// Structs & Enums
    /// -----------------------------------------------------------------------
    struct RetirementRequest {
        address user;
        uint256 amount;        // in base chain currency
        uint32 dstChainId;
        string carbonType;
        address beneficiary;
        bool completed;
        bytes proof;
    }

    /// -----------------------------------------------------------------------
    /// State Variables
    /// -----------------------------------------------------------------------

    // LayerZero endpoint on Base
    ILayerZeroEndpoint public lzEndpoint;

    // USDC on Base
    IERC20Upgradeable public usdc;

    // Fee in basis points (default 100 = 1%)
    uint16 public feeBps;

    // auto-incremented request counter
    uint256 public nextRequestId;

    // Mapping requestId => RetirementRequest
    mapping(uint256 => RetirementRequest) public requests;

    // Supported carbon credit types
    mapping(string => bool) public supportedCarbonTypes;

    // Supported destination chain IDs
    mapping(uint32 => bool) public supportedChains;

    /// -----------------------------------------------------------------------
    /// Modifiers
    /// -----------------------------------------------------------------------
    modifier onlyEndpoint() {
        if (msg.sender != address(lzEndpoint)) revert NotLZEndpoint();
        _;
    }

    /// -----------------------------------------------------------------------
    /// Initialization
    /// -----------------------------------------------------------------------
    /**
     * @dev Initialize contract with LayerZero endpoint & USDC address.
     * @param _endpoint LayerZero Endpoint
     * @param _usdc USDC token address on Base
     */
    function initialize(address _endpoint, address _usdc) external initializer {
        if (_endpoint == address(0) || _usdc == address(0)) revert ZeroAddress();
        __Ownable_init();
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        lzEndpoint = ILayerZeroEndpoint(_endpoint);
        usdc = IERC20Upgradeable(_usdc);
        feeBps = 100; //1%
        nextRequestId = 1;

        // Mainnet chains: Celo = 26, Polygon = 109
        supportedChains[26] = true;  // Celo Mainnet
        supportedChains[42220] = true;  // LZ Celo Mainnet
        supportedChains[109] = true; // Polygon Mainnet
        
        // Testnet chains: Celo Alfajores = 44787, Polygon Amoy = 80002
        supportedChains[44787] = true; // Celo Alfajores
        supportedChains[80002] = true; // Polygon Amoy

        // default supported carbon types
        supportedCarbonTypes["toucan_bct"] = true;
        supportedCarbonTypes["toucan_nct"] = true;
        supportedCarbonTypes["klima_bct"] = true;
    }

    /// -----------------------------------------------------------------------
    /// Public Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Initiate a carbon credit retirement request.
     * @dev User must send native ETH (msg.value) for ETH highways or approve & transfer USDC before calling for USDC.
     * @param useUSDC Set true to use USDC, false for native ETH
     * @param amount Amount of ETH or USDC to use
     * @param dstChainId Destination chain ID (Polygon or Celo)
     * @param carbonType Type of carbon credit to retire
     * @param beneficiary Address on destination chain to retire to
     * @param destinationAddress The address of the contract on the destination chain
     */
    function initiateRetirement(
        bool useUSDC,
        uint256 amount,
        uint32 dstChainId,
        string calldata carbonType,
        address beneficiary,
        bytes calldata destinationAddress
    ) external payable nonReentrant returns (uint256 requestId) {
        if (amount == 0) revert ZeroAmount();
        if (!supportedChains[dstChainId]) revert InvalidChain();
        if (!supportedCarbonTypes[carbonType]) revert InvalidCarbonType();
        if (beneficiary == address(0)) revert ZeroAddress();

        // handle payment
        uint256 paid;
        if (useUSDC) {
            // user must have approved
            usdc.transferFrom(msg.sender, address(this), amount);
            paid = amount;
        } else {
            // native ETH
            if (msg.value != amount) revert InsufficientValue();
            paid = msg.value;
        }

        // compute and deduct fees
        uint256 fee = (paid * feeBps) / 10_000;
        uint256 netAmount = paid - fee;

        // store request
        requestId = nextRequestId++;
        RetirementRequest storage r = requests[requestId];
        r.user = msg.sender;
        r.amount = netAmount;
        r.dstChainId = dstChainId;
        r.carbonType = carbonType;
        r.beneficiary = beneficiary;

        // encode payload: abi.encode(requestId, user, netAmount, carbonType, beneficiary)
        bytes memory payload = abi.encode(
            requestId,
            msg.sender,
            netAmount,
            carbonType,
            beneficiary
        );

        // send cross-chain message
        // Check if the chain ID fits within uint16 range for LayerZero compatibility
        if (dstChainId > type(uint16).max) revert InvalidChain();
        
        lzEndpoint.send{value: msg.value - (useUSDC ? 0 : fee)}(
            uint16(dstChainId),
            destinationAddress,  // Use the provided destination address
            payload,
            payable(msg.sender),
            address(0),
            bytes("")
        );

        emit RetirementInitiated(requestId, msg.sender, netAmount, dstChainId, carbonType, beneficiary);
    }

    /**
     * @inheritdoc ILayerZeroReceiver
     * @dev LayerZero endpoint calls this when a message arrives from another chain.
     *      Expected payload: abi.encode(requestId, completedProof)
     */
    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64,
        bytes calldata payload
    ) external override onlyEndpoint nonReentrant {
        (uint256 requestId, bytes memory proof) = abi.decode(payload, (uint256, bytes));
        RetirementRequest storage r = requests[requestId];

        if (r.completed) revert AlreadyCompleted();
        r.completed = true;
        r.proof = proof;

        // Convert uint16 to uint32 for our internal representation
        uint32 srcChainId = uint32(_srcChainId);
        emit RetirementCompleted(requestId, r.user, r.amount, srcChainId, proof);
    }

    /// -----------------------------------------------------------------------
    /// View Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Returns status & proof of a given request
     * @param requestId The retirement request ID
     */
    function getRequest(uint256 requestId)
        external
        view
        returns (
            address user,
            uint256 amount,
            uint32 dstChainId,
            string memory carbonType,
            address beneficiary,
            bool completed,
            bytes memory proof
        )
    {
        RetirementRequest storage r = requests[requestId];
        return (r.user, r.amount, r.dstChainId, r.carbonType, r.beneficiary, r.completed, r.proof);
    }

    /// -----------------------------------------------------------------------
    /// Admin Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Withdraw accumulated fees (ETH & USDC) to owner
     * @param to Recipient address
     */
    function withdrawFees(address payable to) external onlyOwner nonReentrant {
        if (to == address(0)) revert ZeroAddress();

        // withdraw native ETH
        uint256 balEth = address(this).balance;
        if (balEth > 0) {
            (bool sent,) = to.call{value: balEth}("");
            if (!sent) revert TransferFailed();
        }

        // withdraw USDC
        uint256 balUsdc = usdc.balanceOf(address(this));
        if (balUsdc > 0) {
            usdc.transfer(to, balUsdc);
        }

        emit FeesWithdrawn(to, balEth + balUsdc);
    }

    /**
     * @notice Owner can add/remove supported carbon types
     */
    function setCarbonType(string calldata carbonType, bool enabled) external onlyOwner {
        supportedCarbonTypes[carbonType] = enabled;
    }

    /**
     * @notice Owner can add/remove supported destination chains
     */
    function setSupportedChain(uint32 chainId, bool enabled) external onlyOwner {
        supportedChains[chainId] = enabled;
    }

    /**
     * @notice Update fee BPS (max 1000 = 10%)
     */
    function setFeeBps(uint16 newFeeBps) external onlyOwner {
        require(newFeeBps <= 1000, "Fee too high");
        feeBps = newFeeBps;
        emit FeePercentUpdated(newFeeBps);
    }

    /**
     * @notice Update LayerZero endpoint
     */
    function setEndpoint(address newEndpoint) external onlyOwner {
        if (newEndpoint == address(0)) revert ZeroAddress();
        lzEndpoint = ILayerZeroEndpoint(newEndpoint);
        emit EndpointUpdated(newEndpoint);
    }

    /// -----------------------------------------------------------------------
    /// Emergency Functions
    /// -----------------------------------------------------------------------

    /**
     * @notice Emergency withdraw stuck tokens or ETH
     */
    function emergencyWithdraw(address token, address to, uint256 amount) external onlyOwner nonReentrant {
        if (to == address(0)) revert ZeroAddress();

        if (token == address(0)) {
            // withdraw ETH
            (bool sent,) = to.call{value: amount}("");
            if (!sent) revert TransferFailed();
        } else {
            IERC20Upgradeable(token).transfer(to, amount);
        }

        emit EmergencyWithdrawn(token, to, amount);
    }

    /// -----------------------------------------------------------------------
    /// Upgradeability
    /// -----------------------------------------------------------------------

    function _authorizeUpgrade(address) internal override onlyOwner {}

    /// -----------------------------------------------------------------------
    /// Fallback to accept native ETH payments
    /// -----------------------------------------------------------------------
    receive() external payable {}
}

contracts/interfaces/ILayerZeroEndpoint..content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

interface ILayerZeroEndpoint {
    // @notice send a LayerZero message to the specified address at a LayerZero endpoint.
    // @param _dstChainId - the destination chain identifier
    // @param _destination - the address on destination chain (in bytes). address length/format may vary by chains
    // @param _payload - a custom bytes payload to send to the destination contract
    // @param _refundAddress - if the source transaction is cheaper than the amount of value passed, refund the additional amount to this address
    // @param _zroPaymentAddress - the address of the ZRO token holder who would pay for the transaction
    // @param _adapterParams - parameters for custom functionality. e.g. receive airdropped native gas from the relayer on destination
    function send(
        uint16 _dstChainId,
        bytes calldata _destination,
        bytes calldata _payload,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes calldata _adapterParams
    ) external payable;

    // @notice used by the messaging library to publish verified payload
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source contract (as bytes) at the source chain
    // @param _dstAddress - the address on destination chain
    // @param _nonce - the unbound message ordering nonce
    // @param _gasLimit - the gas limit for external contract execution
    // @param _payload - verified payload to send to the destination contract
    function receivePayload(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        address _dstAddress,
        uint64 _nonce,
        uint _gasLimit,
        bytes calldata _payload
    ) external;

    // @notice get the inboundNonce of a receiver from a source chain which could be EVM or non-EVM chain
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    function getInboundNonce(uint16 _srcChainId, bytes calldata _srcAddress) external view returns (uint64);

    // @notice get the outboundNonce from this source chain which, consequently, is always an EVM
    // @param _srcAddress - the source chain contract address
    function getOutboundNonce(uint16 _dstChainId, address _srcAddress) external view returns (uint64);

    // @notice gets a quote in source native gas, for the amount that send() requires to pay for message delivery
    // @param _dstChainId - the destination chain identifier
    // @param _userApplication - the user app address on this EVM chain
    // @param _payload - the custom message to send over LayerZero
    // @param _payInZRO - if false, user app pays the protocol fee in native token
    // @param _adapterParam - parameters for the adapter service, e.g. send some dust native token to dstChain
    function estimateFees(
        uint16 _dstChainId,
        address _userApplication,
        bytes calldata _payload,
        bool _payInZRO,
        bytes calldata _adapterParam
    ) external view returns (uint nativeFee, uint zroFee);

    // @notice get this Endpoint's immutable source identifier
    function getChainId() external view returns (uint16);

    // @notice the interface to retry failed message on this Endpoint destination
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    // @param _payload - the payload to be retried
    function retryPayload(uint16 _srcChainId, bytes calldata _srcAddress, bytes calldata _payload) external;

    // @notice query if any STORED payload (message blocking) at the endpoint.
    // @param _srcChainId - the source chain identifier
    // @param _srcAddress - the source chain contract address
    function hasStoredPayload(uint16 _srcChainId, bytes calldata _srcAddress) external view returns (bool);

    // @notice query if the _libraryAddress is valid for sending msgs.
    // @param _userApplication - the user app address on this EVM chain
    function getSendLibraryAddress(address _userApplication) external view returns (address);

    // @notice query if the _libraryAddress is valid for receiving msgs.
    // @param _userApplication - the user app address on this EVM chain
    function getReceiveLibraryAddress(address _userApplication) external view returns (address);

    // @notice query if the non-reentrancy guard for send() is on
    // @return true if the guard is on. false otherwise
    function isSendingPayload() external view returns (bool);

    // @notice query if the non-reentrancy guard for receive() is on
    // @return true if the guard is on. false otherwise
    function isReceivingPayload() external view returns (bool);

    // @notice get the configuration of the LayerZero messaging library of the specified version
    // @param _version - messaging library version
    // @param _chainId - the chainId for the pending config change
    // @param _userApplication - the contract address of the user application
    // @param _configType - type of configuration. every messaging library has its own convention.
    function getConfig(
        uint16 _version,
        uint16 _chainId,
        address _userApplication,
        uint _configType
    ) external view returns (bytes memory);

    // @notice get the send() LayerZero messaging library version
    // @param _userApplication - the contract address of the user application
    function getSendVersion(address _userApplication) external view returns (uint16);

    // @notice get the lzReceive() LayerZero messaging library version
    // @param _userApplication - the contract address of the user application
    function getReceiveVersion(address _userApplication) external view returns (uint16);
}

contracts/interfaces/ILayerZeroReceiver..content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

interface ILayerZeroReceiver {
    // @notice LayerZero endpoint will invoke this function to deliver the message on the destination
    // @param _srcChainId - the source endpoint identifier
    // @param _srcAddress - the source sending contract address from the source chain
    // @param _nonce - the ordered message nonce
    // @param _payload - the signed payload is the UA bytes has encoded to be sent
    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) external;
}

contracts/klima.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/interfaces/IERC1820Registry.sol";
import "@openzeppelin/contracts/interfaces/IERC777Recipient.sol";

/**
 * @title IKlimaRetirementAggregator
 * @notice Interface for the KlimaDAO Retirement Aggregator Diamond contract
 */
interface IKlimaRetirementAggregator {
    function retireExactSourceDefault(
        address sourceToken,
        address poolToken,
        uint256 maxAmountIn,
        string memory retiringEntityString,
        address beneficiaryAddress,
        string memory beneficiaryString,
        string memory retirementMessage,
        uint8 fromMode
    ) external returns (uint256);
    
    function isPoolToken(address poolToken) external view returns (bool);
}

/**
 * @title AcrossCarbonRetirementReceiver
 * @notice Contract that receives tokens from Across Protocol and automatically retires carbon
 * @dev This contract is designed to be the recipient of bridged tokens from Across Protocol
 */
contract AcrossCarbonRetirementReceiver is Ownable, ReentrancyGuard, IERC777Recipient {
    // ERC1820 registry address (same on all chains)
    IERC1820Registry private constant ERC1820_REGISTRY = IERC1820Registry(0x1820a4B7618BdE71Dce8cdc73aAB6C95905faD24);
    
    // Interface hash for ERC777TokensRecipient
    bytes32 private constant TOKENS_RECIPIENT_INTERFACE_HASH = keccak256("ERC777TokensRecipient");
    // KlimaDAO Aggregator Diamond contract address
    address public constant KLIMA_AGGREGATOR = 0x8cE54d9625371fb2a068986d32C85De8E6e995f8;
    
    // USDC token address on Polygon
    address public constant USDC_TOKEN = 0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174;
    
    // Default pool token (BCT)
    address public constant DEFAULT_POOL_TOKEN = 0x2F800Db0fdb5223b3C3f354886d907A671414A7F;
    
    // Across Protocol's SpokePool on Polygon
    address public constant ACROSS_SPOKE_POOL = 0x9295ee1d8C5b022Be115A2AD3c30C72E34e7F096;
    
    // Across Protocol's Multicall Handler on Polygon
    address public constant MULTICALL_HANDLER_ADDRESS = 0x924a9f036260DdD5808007E1AA95f08eD08aA569;
    
    // Events
    event RetirementExecuted(
        address indexed beneficiary,
        string beneficiaryName,
        address poolToken,
        uint256 usdcAmount,
        uint256 carbonAmount
    );
    
    event RetirementFailed(
        address indexed beneficiary,
        address poolToken,
        uint256 usdcAmount,
        string reason
    );
    
    /**
     * @notice Constructor
     */
    constructor() Ownable(msg.sender) {
        // Approve the KlimaDAO Aggregator to spend USDC
        // This is done once at deployment to save gas on future transactions
        IERC20(USDC_TOKEN).approve(KLIMA_AGGREGATOR, type(uint256).max);
        
        // Register with ERC1820 registry to receive ERC777 tokens
        ERC1820_REGISTRY.setInterfaceImplementer(
            address(this),
            TOKENS_RECIPIENT_INTERFACE_HASH,
            address(this)
        );
    }
    
    /**
     * @notice Decode the message from Across Protocol
     * @param message The encoded message from Across
     * @return beneficiary The beneficiary address
     * @return beneficiaryName The beneficiary name
     * @return poolToken The pool token address
     */
    function decodeMessage(bytes memory message) internal view returns (
        address beneficiary,
        string memory beneficiaryName,
        address poolToken
    ) {
        // Default values if parsing fails
        beneficiary = address(0);
        beneficiaryName = "OffsetZap User";
        poolToken = DEFAULT_POOL_TOKEN;
        
        // Try to decode the JSON message
        // Format expected: {"sender":"0x...","amount":"1000000","beneficiary":"0x...","poolTokenAddress":"0x...","beneficiaryName":"Name"}
        if (message.length > 0) {
            // Simple parsing - extract values between specific markers
            // This is a basic implementation - in production you might want a more robust parser
            
            // Find beneficiary
            bytes memory beneficiaryMarker = bytes(',"beneficiary":"');
            int256 beneficiaryPos = indexOf(message, beneficiaryMarker);
            if (beneficiaryPos >= 0) {
                beneficiary = extractAddress(message, uint256(beneficiaryPos) + beneficiaryMarker.length);
            }
            
            // Find poolTokenAddress
            bytes memory poolTokenMarker = bytes(',"poolTokenAddress":"');
            int256 poolTokenPos = indexOf(message, poolTokenMarker);
            if (poolTokenPos >= 0) {
                poolToken = extractAddress(message, uint256(poolTokenPos) + poolTokenMarker.length);
            }
            
            // Find beneficiaryName
            bytes memory nameMarker = bytes(',"beneficiaryName":"');
            int256 namePos = indexOf(message, nameMarker);
            if (namePos >= 0) {
                beneficiaryName = extractString(message, uint256(namePos) + nameMarker.length);
            }
        }
        
        // Validate beneficiary
        if (beneficiary == address(0)) {
            beneficiary = msg.sender;
        }
        
        // Validate pool token - if invalid, use default
        if (!isValidPoolToken(poolToken)) {
            poolToken = DEFAULT_POOL_TOKEN;
        }
    }
    
    /**
     * @notice Check if a token is a valid pool token
     * @param poolToken The pool token address to check
     * @return isValid True if the token is a valid pool token
     */
    function isValidPoolToken(address poolToken) internal pure returns (bool) {
        // For simplicity, we'll accept any non-zero address as valid
        // In a production environment, you would want to check against a list of known pool tokens
        // or call the KlimaDAO Aggregator's isPoolToken function through an interface
        return poolToken != address(0);
    }
    
    /**
     * @notice Extract an address from a byte array starting at a specific position
     * @param data The byte array
     * @param startPos The starting position
     * @return addr The extracted address
     */
    function extractAddress(bytes memory data, uint256 startPos) internal pure returns (address addr) {
        bytes memory addrBytes = new bytes(42); // 0x + 40 hex chars
        for (uint256 i = 0; i < 42 && startPos + i < data.length; i++) {
            addrBytes[i] = data[startPos + i];
        }
        
        // Convert bytes to address
        // This is a simplified implementation - in production you'd use a more robust parser
        bytes memory hexBytes = new bytes(20);
        for (uint256 i = 0; i < 20; i++) {
            uint8 high = uint8(addrBytes[2*i + 2]) - (uint8(addrBytes[2*i + 2]) >= 65 ? 55 : 48);
            uint8 low = uint8(addrBytes[2*i + 3]) - (uint8(addrBytes[2*i + 3]) >= 65 ? 55 : 48);
            hexBytes[i] = bytes1(high * 16 + low);
        }
        
        assembly {
            addr := mload(add(hexBytes, 32))
        }
    }
    
    /**
     * @notice Extract a string from a byte array starting at a specific position
     * @param data The byte array
     * @param startPos The starting position
     * @return result The extracted string
     */
    function extractString(bytes memory data, uint256 startPos) internal pure returns (string memory result) {
        // Find the closing quote
        uint256 endPos = startPos;
        while (endPos < data.length && data[endPos] != '"') {
            endPos++;
        }
        
        // Extract the string
        bytes memory strBytes = new bytes(endPos - startPos);
        for (uint256 i = 0; i < endPos - startPos; i++) {
            strBytes[i] = data[startPos + i];
        }
        
        return string(strBytes);
    }
    
    /**
     * @notice Find the position of a substring in a string
     * @param data The byte array to search in
     * @param pattern The pattern to search for
     * @return position The position of the pattern, or -1 if not found
     */
    function indexOf(bytes memory data, bytes memory pattern) internal pure returns (int256 position) {
        if (pattern.length == 0) return 0;
        if (data.length < pattern.length) return -1;
        
        for (uint256 i = 0; i <= data.length - pattern.length; i++) {
            bool found = true;
            for (uint256 j = 0; j < pattern.length; j++) {
                if (data[i + j] != pattern[j]) {
                    found = false;
                    break;
                }
            }
            if (found) return int256(i);
        }
        
        return -1;
    }
    
    /**
     * @notice Execute carbon retirement using the KlimaDAO Aggregator
     * @param usdcAmount The amount of USDC to use for retirement
     * @param beneficiary The beneficiary address
     * @param beneficiaryName The beneficiary name
     * @param poolToken The pool token address
     */
    function executeRetirement(
        uint256 usdcAmount,
        address beneficiary,
        string memory beneficiaryName,
        address poolToken
    ) internal {
        // Define the KlimaDAO Aggregator interface for the function we need
        IKlimaRetirementAggregator klimaAggregator = IKlimaRetirementAggregator(KLIMA_AGGREGATOR);
        
        // Try to call the KlimaDAO Aggregator to retire carbon
        try klimaAggregator.retireExactSourceDefault(
            USDC_TOKEN,           // sourceToken (USDC)
            poolToken,            // poolToken (BCT/NCT)
            usdcAmount,           // amount of USDC to use
            "OffsetZap",          // retiring entity
            beneficiary,          // beneficiary address
            beneficiaryName,      // beneficiary name
            "Carbon offset via OffsetZap", // retirement message
            0                     // fromMode (FROM_WALLET)
        ) returns (uint256 carbonAmount) {
            // Success! The call to KlimaDAO Aggregator went through
                
                emit RetirementExecuted(
                    beneficiary,
                    beneficiaryName,
                    poolToken,
                    usdcAmount,
                    carbonAmount
                );
        } catch Error(string memory reason) {
            // Solidity revert with reason
            emit RetirementFailed(
                beneficiary,
                poolToken,
                usdcAmount,
                reason
            );
        } catch (bytes memory) {
            // Low-level revert without reason
            emit RetirementFailed(
                beneficiary,
                poolToken,
                usdcAmount,
                "Unknown error"
            );
        }
    }
    
    /**
     * @notice Function to handle token transfers via ERC777 tokensReceived hook
     * @param operator The address which called the token transfer
     * @param from The address which previously owned the token
     * @param to The address which will own the token
     * @param amount The amount of tokens being transferred
     * @param userData Additional user data sent with the token transfer
     * @param operatorData Additional operator data sent with the token transfer
     */
    /**
     * @notice Implementation of IERC777Recipient interface
     */
    function tokensReceived(
        address operator,
        address from,
        address to,
        uint256 amount,
        bytes calldata userData,
        bytes calldata operatorData
    ) external override nonReentrant {
        // Only accept tokens from the Across SpokePool and ensure we're the recipient
        if (from == ACROSS_SPOKE_POOL && to == address(this)) {
            // Try to decode userData as a retirement message
            address beneficiary;
            string memory beneficiaryName;
            address poolToken;
            
            // If userData is present and valid, use it to extract parameters
            if (userData.length > 0) {
                (beneficiary, beneficiaryName, poolToken) = decodeMessage(userData);
            } else {
                // Default values if no userData
                beneficiary = from;
                beneficiaryName = "OffsetZap User";
                poolToken = DEFAULT_POOL_TOKEN;
            }
            
            // Execute the retirement
            executeRetirement(amount, beneficiary, beneficiaryName, poolToken);
        }
        // No return needed as function is void
    }
    
    /**
     * @notice Legacy function for compatibility with Across Protocol
     * @param recipient The recipient address (should be this contract)
     * @param amount The amount of tokens received
     * @param message The message sent with the tokens
     */
    function handleAcrossTransfer(
        address recipient,
        uint256 amount,
        bytes calldata message
    ) external nonReentrant {
        // Only Across SpokePool can call this function
        require(
            msg.sender == ACROSS_SPOKE_POOL || 
            msg.sender == MULTICALL_HANDLER_ADDRESS, 
            "Only Across SpokePool or multicall handler can call"
        );
        require(recipient == address(this), "Recipient must be this contract");
        
        // Check USDC balance
        uint256 usdcBalance = IERC20(USDC_TOKEN).balanceOf(address(this));
        require(usdcBalance >= amount, "Insufficient USDC balance");
        
        // Decode the message to get retirement parameters
        (
            address beneficiary,
            string memory beneficiaryName,
            address poolToken
        ) = decodeMessage(message);
        
        // Execute the retirement
        executeRetirement(amount, beneficiary, beneficiaryName, poolToken);
    }
    
    /**
     * @notice Fallback function to handle direct token transfers
     * @dev This allows the contract to receive tokens directly
     */
    receive() external payable {
        // Accept ETH transfers
    }
    
    /**
     * @notice Function to manually retire carbon if automatic retirement fails
     * @param usdcAmount The amount of USDC to use
     * @param beneficiary The beneficiary address
     * @param beneficiaryName The beneficiary name
     * @param poolToken The pool token address
     */
    function manualRetire(
        uint256 usdcAmount,
        address beneficiary,
        string memory beneficiaryName,
        address poolToken
    ) external onlyOwner nonReentrant {
        // Check USDC balance
        uint256 usdcBalance = IERC20(USDC_TOKEN).balanceOf(address(this));
        require(usdcBalance >= usdcAmount, "Insufficient USDC balance");
        
        // Execute the retirement
        executeRetirement(usdcAmount, beneficiary, beneficiaryName, poolToken);
    }
    
    /**
     * @notice Function to recover tokens if needed
     * @param token The token address to recover
     * @param amount The amount to recover
     */
    function recoverTokens(address token, uint256 amount) external onlyOwner nonReentrant {
        IERC20(token).transfer(owner(), amount);
    }
    
    /**
     * @notice Function to recover ETH if needed
     */
    function recoverEth() external onlyOwner nonReentrant {
        payable(owner()).transfer(address(this).balance);
    }
}

